mapper.select:
SELECT CODE_ID, CODE, CODE_NM, CODE_DC, USE_AT, CONVERT(VARCHAR(19), FRST_REGIST_PNTTM, 120) AS FRST_REGIST_PNTTM, FRST_REGISTER_ID, CONVERT(VARCHAR(19), LAST_UPDT_PNTTM, 120) AS LAST_UPDT_PNTTM, LAST_UPDUSR_ID FROM MES_CCMMNDETAIL_CODE WHERE CODE_ID = #{codeId}
AND USE_AT = #{useAt}
ORDER BY CODE

mapper.update:
UPDATE MES_CCMMNDETAIL_CODE
SET
	CODE_NM = #{codeNm},
	CODE_DC = #{codeDc},
	USE_AT = #{useAt},
	LAST_UPDT_PNTTM = GETDATE(),
	LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE CODE_ID = #{codeId}
AND CODE = #{code}

mapper.delete:
DELETE FROM MES_CCMMNDETAIL_CODE
WHERE CODE_ID = #{codeId}
AND CODE = #{code}

mapper.select:
SELECT COUNT(*) AS CNT
FROM MES_CCMMNDETAIL_CODE
WHERE CODE_ID = #{codeId}
AND CODE = #{code}
