mapper.select:
SELECT PROCESS_DEFECT_ID, PROCESS_ID, DEFECT_CODE, DEFECT_NAME, DEFECT_TYPE, DESCRIPTION, USE_YN, REG_USER_ID, CONVERT(VARCHAR(19), REG_DT, 120) AS REG_DT, UPD_USER_ID, CONVERT(VARCHAR(19), UPD_DT, 120) AS UPD_DT FROM TB_PROCESS_DEFECT WHERE 1=1
AND PROCESS_ID = #{processId}
AND USE_YN = #{useYn}
ORDER BY DEFECT_CODE ASC
