mapper.select:
SELECT PROCESS_INSPECTION_ID, PROCESS_ID, INSPECTION_CODE, INSPECTION_NAME, INSPECTION_TYPE, STANDARD_VALUE, UPPER_LIMIT, LOWER_LIMIT, UNIT, DESCRIPTION, USE_YN, REG_USER_ID, CONVERT(VARCHAR(19), REG_DT, 120) AS REG_DT, UPD_USER_ID, CONVERT(VARCHAR(19), UPD_DT, 120) AS UPD_DT FROM TB_PROCESS_INSPECTION WHERE 1=1
AND PROCESS_ID = #{processId}
AND USE_YN = #{useYn}
ORDER BY INSPECTION_CODE ASC
