mapper.select:
SELECT pw.PROCESS_WORKPLACE_ID, pw.PROCESS_ID, pw.WORKPLACE_ID, pw.WORKPLACE_NAME, p.PROCESS_CODE, p.PROCESS_NAME, pw.USE_YN, pw.REG_USER_ID, CONVERT(VARCHAR(19), pw.REG_DT, 120) AS REG_DT, pw.UPD_USER_ID, CONVERT(VARCHAR(19), pw.UPD_DT, 120) AS UPD_DT FROM TB_PROCESS_WORKPLACE pw LEFT JOIN TB_PROCESS p ON pw.PROCESS_ID = p.PROCESS_ID WHERE 1=1
AND pw.PROCESS_ID = #{processId}
AND pw.WORKPLACE_ID = #{workplaceId}
AND pw.USE_YN = #{useYn}
ORDER BY pw.REG_DT
