mapper.select:
SELECT FACTORY_CODE, WORKCENTER_CODE, WORK_CODE, WORK_NAME, WW_ID, USE_YN, OPMAN_CODE, CONVERT(VARCHAR(19), OPTIME, 120) AS OPTIME, OPMAN_CODE2, CONVERT(VARCHAR(19), OPTIME2, 120) AS OPTIME2 FROM TPR103 WHERE 1=1
AND WORKCENTER_CODE = #{workplaceCode}
AND WORK_CODE = #{processCode}
AND USE_YN = #{useYn}
ORDER BY OPTIME

mapper.delete:
DELETE FROM TPR103
WHERE WORKCENTER_CODE = #{workplaceCode}
  AND WORK_CODE = #{processCode}
