mapper.select:
SELECT COUNT(*) AS CNT FROM TB_WORKPLACE WHERE 1=1
AND WORKPLACE_CODE LIKE '%' + #{searchWrd} + '%'
AND WORKPLACE_NAME LIKE '%' + #{searchWrd} + '%'
AND LOCATION LIKE '%' + #{searchWrd} + '%'
AND STATUS = #{status}
AND USE_YN = #{useYn}

SELECT COUNT(*) AS CNT
FROM TB_WORKPLACE
WHERE WORKPLACE_CODE = #{workplaceCode}

SELECT COUNT(*) AS CNT
FROM TB_WORKPLACE
WHERE WORKPLACE_CODE = #{workplaceCode}
  AND WORKPLACE_ID != #{workplaceId}
