mapper.select:
SELECT ISNULL(A.ORDER_FLAG, 'PLANNED') AS ORDER_FLAG, R.ORDER_NO, R.ORDER_HISTNO, R.ORDER_SEQNO, CASE WHEN R.ORDER_NO IS NOT NULL THEN '수주' ELSE '재고' END AS ORDER_GUBUN, CASE WHEN R.ORDER_NO IS NOT NULL THEN 0 ELSE 1 END AS ORDER_GUBUN_FLAG, A.FACTORY_CODE, A.PRODPLAN_DATE, A.PRODPLAN_SEQ, A.PRODWORK_SEQ, A.PRODPLAN_ID, A.PROD_DATE, A.ITEM_CODE AS ITEM_CODE_ID, B1.MATERIAL_CODE AS ITEM_CODE, A.ITEM_NAME, A.WORKCENTER_CODE, C.WORKCENTER_NAME, A.WORK_CODE, A.EQUIP_SYS_CD, D.EQUIPMENT_NAME, A.WORKER_TYPE, A.WORKER_CODE, A.WORKER_NAME, A.LOT_NO, A.PROD_QTY, A.WORKORDER_SEQ, A.BIGO, A.SEL_CUSTOMER_NAMES, A.OPMAN_CODE, A.OPTIME, A.OPMAN_CODE2, A.OPTIME2 FROM TPR301 A LEFT JOIN TPR301R R ON A.FACTORY_CODE = R.FACTORY_CODE AND A.PRODPLAN_DATE = R.PRODPLAN_DATE AND A.PRODPLAN_SEQ = R.PRODPLAN_SEQ LEFT JOIN TCO403 B1 ON A.ITEM_CODE = B1.MATERIAL_ID LEFT JOIN TPR101 C ON A.WORKCENTER_CODE = C.WORKCENTER_CODE LEFT JOIN TPR151 D ON A.EQUIP_SYS_CD = D.EQUIP_SYS_CD WHERE 1=1 AND CONVERT(date, A.PRODPLAN_DATE, 112) BETWEEN #{dateFrom} AND #{dateTo}
AND A.WORKCENTER_CODE = #{workplace}
AND A.EQUIP_SYS_CD = #{equipment}
AND ISNULL(A.ORDER_FLAG, 'PLANNED') = #{orderFlag}
ORDER BY A.PRODPLAN_DATE, A.PRODPLAN_SEQ, A.PRODWORK_SEQ OFFSET #{offset} ROWS FETCH NEXT #{size} ROWS ONLY

SELECT COUNT(*)
FROM TPR301 A
WHERE 1=1
AND CONVERT(date, A.PRODPLAN_DATE, 112) BETWEEN #{dateFrom} AND #{dateTo}

	AND A.WORKCENTER_CODE = #{workplace}


	AND A.EQUIP_SYS_CD = #{equipment}


	AND ISNULL(A.ORDER_FLAG, 'PLANNED') = #{orderFlag}

SELECT COUNT(*)+1
FROM TPR504
WHERE FACTORY_CODE = '000001'
AND PRODPLAN_DATE = #{prodplanDate}
AND PRODPLAN_SEQ = #{prodplanSeq}
AND PRODWORK_SEQ = #{prodworkSeq}

mapper.update:
UPDATE TPR301 SET ORDER_FLAG = #{orderFlag}
WHERE FACTORY_CODE = '000001'
AND PRODPLAN_DATE = #{prodplanDate}
AND PRODPLAN_SEQ = #{prodplanSeq}
AND PRODWORK_SEQ = #{prodworkSeq}

UPDATE TPR504 SET DELETE_FLAG = 1,
                  OPTIME2 = GETDATE(),
                  OPMAN_CODE2 = #{opmanCode}
WHERE FACTORY_CODE = '000001'
AND PRODPLAN_DATE = #{prodplanDate}
AND PRODPLAN_SEQ = #{prodplanSeq}
AND PRODWORK_SEQ = #{prodworkSeq}

mapper.select:
SELECT COUNT(*)
FROM TPR601
WHERE FACTORY_CODE = '000001'
AND PRODPLAN_DATE = #{prodplanDate}
AND PRODPLAN_SEQ = #{prodplanSeq}
AND PRODWORK_SEQ = #{prodworkSeq}

mapper.update:
UPDATE TPR504 SET WORKDT_DATE = #{workdtDate},
				  PROD_QTY = #{orderQty},
				  WORKORDER_SEQ = #{newWorkorderSeq},
				  BIGO = #{bigo},
				  OPMAN_CODE2 = #{opmanCode},
				  OPTIME2 = GETDATE()
WHERE FACTORY_CODE = '000001'
  AND PRODPLAN_DATE = #{prodplanDate}
  AND PRODPLAN_SEQ = #{prodplanSeq}
  AND WORK_SEQ = #{orderSeq}

mapper.select:
SELECT COUNT(*)
FROM TPR504
WHERE FACTORY_CODE = '000001'
  AND PRODPLAN_DATE = #{prodplanDate}
  AND PRODPLAN_SEQ = #{prodplanSeq}
  AND PRODWORK_SEQ  = #{prodworkSeq}
  AND ISNULL(DELETE_FLAG, 0) != 1
