mapper.select:
SELECT X.ORDER_NO, X.ORDER_SEQNO, X.ORDER_HISTNO, X.CUSTOMER_CODE, X1.CUSTOMER_NAME , A.FACTORY_CODE , A.PRODPLAN_ID , A.PRODPLAN_DATE , A.PRODPLAN_SEQ , A.PRODWORK_SEQ , CAST(A.WORK_SEQ AS INT) AS WORK_SEQ , A.WORKDT_DATE , A1.WORKCENTER_CODE , A2.WORKCENTER_NAME , A.WORK_CODE , B.WORK_NAME , ISNULL(A.ITEM_CODE, '') AS ITEM_CODE_ID , ISNULL(C.MATERIAL_CODE, '') AS ITEM_CODE , ISNULL(C.MATERIAL_NAME, '') AS ITEM_NAME , ISNULL(A.PROD_CODE, '') AS PROD_CODE_ID , ISNULL(C1.MATERIAL_CODE, '') AS PROD_CODE , ISNULL(C1.MATERIAL_NAME, '') AS PROD_NAME , ISNULL(C1.MATERIAL_SPEC, '') AS PROD_SPEC , ISNULL(A.EQUIP_SYS_CD, '') AS EQUIP_SYS_CD , ISNULL(D.EQUIPMENT_NAME, '') AS EQUIP_SYS_CD_NM , A.PROD_QTY , A.ORDER_FLAG , A.WORKORDER_SEQ , A.LOT_NO , A.ERP_SEND_FLAG , A.ERP_WORKDT_IDX , A.OPMAN_CODE , A.OPTIME , A.OPMAN_CODE2 , A.OPTIME2 , A.TPR504ID , A.PRODORDER_ID FROM TPR504 A LEFT JOIN TPR301 A1 ON A.FACTORY_CODE = A1.FACTORY_CODE AND A.PRODPLAN_DATE = A1.PRODPLAN_DATE AND A.PRODPLAN_SEQ = A1.PRODPLAN_SEQ LEFT JOIN TPR101 A2 ON A1.WORKCENTER_CODE = A2.WORKCENTER_CODE LEFT JOIN TPR102 B ON A.FACTORY_CODE = B.FACTORY_CODE AND A.WORK_CODE = B.WORK_CODE LEFT JOIN TCO403 C ON A.ITEM_CODE = C.MATERIAL_ID LEFT JOIN TCO403 C1 ON A.PROD_CODE = C1.MATERIAL_ID LEFT JOIN TPR151 D ON A.EQUIP_SYS_CD = D.EQUIP_SYS_CD LEFT JOIN TPR301R X --수주정보 ON A1.PRODPLAN_DATE = X.PRODPLAN_DATE AND A1.PRODPLAN_SEQ = X.PRODPLAN_SEQ AND X.ORDER_HISTNO = 1 LEFT JOIN TCO601 X1 ON X.CUSTOMER_CODE = X1.CUSTOMER_CODE WHERE 1=1 AND A.WORKDT_DATE BETWEEN #{dateFrom} AND #{dateTo}
AND A1.WORKCENTER_CODE = #{workplace}
AND A.EQUIP_SYS_CD = #{equipment}
AND ( A.ITEM_CODE LIKE '%' + #{keyword} + '%' OR C.MATERIAL_NAME LIKE '%' + #{keyword} + '%' OR D.EQUIPMENT_NAME LIKE '%' + #{keyword} + '%' OR B.WORK_NAME LIKE '%' + #{keyword} + '%' OR A.PRODPLAN_ID LIKE '%' + #{keyword} + '%' )
ORDER BY ORDER_NO, ORDER_SEQNO, ORDER_HISTNO, A.PRODPLAN_DATE, A.PRODPLAN_SEQ, A.PRODWORK_SEQ, A.WORK_SEQ, A.WORKDT_DATE DESC OFFSET #{offset} ROWS FETCH NEXT #{size} ROWS ONLY

SELECT COUNT(*) FROM TPR504 A LEFT JOIN TPR301 A1 ON A.FACTORY_CODE = A1.FACTORY_CODE AND A.PRODPLAN_DATE = A1.PRODPLAN_DATE AND A.PRODPLAN_SEQ = A1.PRODPLAN_SEQ LEFT JOIN TPR101 A2 ON A1.WORKCENTER_CODE = A2.WORKCENTER_CODE LEFT JOIN TPR102 B ON A.FACTORY_CODE = B.FACTORY_CODE AND A.WORK_CODE = B.WORK_CODE LEFT JOIN TCO403 C ON A.ITEM_CODE = C.MATERIAL_ID LEFT JOIN TPR151 D ON A.EQUIP_SYS_CD = D.EQUIP_SYS_CD LEFT JOIN TPR301R X --수주정보 ON A1.PRODPLAN_DATE = X.PRODPLAN_DATE AND A1.PRODPLAN_SEQ = X.PRODPLAN_SEQ AND X.ORDER_HISTNO = 1 LEFT JOIN TCO601 X1 ON X.CUSTOMER_CODE = X1.CUSTOMER_CODE WHERE 1=1 AND A.WORKDT_DATE BETWEEN #{dateFrom} AND #{dateTo}
AND A1.WORKCENTER_CODE = #{workplace}
AND A.EQUIP_SYS_CD = #{equipment}
AND ( A.ITEM_CODE LIKE '%' + #{keyword} + '%' OR C.MATERIAL_NAME LIKE '%' + #{keyword} + '%' OR D.EQUIPMENT_NAME LIKE '%' + #{keyword} + '%' OR B.WORK_NAME LIKE '%' + #{keyword} + '%' OR A.PRODPLAN_ID LIKE '%' + #{keyword} + '%' )

SELECT COUNT(*)+1
FROM TPR601
WHERE FACTORY_CODE = #{FACTORY_CODE}
AND PRODPLAN_DATE = #{PRODPLAN_DATE}
AND PRODPLAN_SEQ = #{PRODPLAN_SEQ}
AND PRODWORK_SEQ = #{PRODWORK_SEQ}
AND WORK_SEQ = #{WORK_SEQ}

mapper.update:
UPDATE TPR601 SET PROD_STIME = #{PROD_STIME},
                  PROD_ETIME = #{PROD_ETIME},
                  PROD_QTY = #{PROD_QTY},
                  GOOD_QTY = #{GOOD_QTY},
                  BAD_QTY = #{BAD_QTY},
                  RCV_QTY = #{RCV_QTY},
                  OPMAN_CODE2 = #{OPMAN_CODE},
                  OPTIME2 = GETDATE()
WHERE FACTORY_CODE = '000001'
AND PRODPLAN_DATE = #{PRODPLAN_DATE}
AND PRODPLAN_SEQ = #{PRODPLAN_SEQ}
AND PRODWORK_SEQ = #{PRODWORK_SEQ}
AND WORK_SEQ = #{WORK_SEQ}
AND PROD_SEQ = #{PROD_SEQ}

mapper.delete:
DELETE FROM TPR601
WHERE FACTORY_CODE = '000001'
  AND PRODPLAN_DATE = #{PRODPLAN_DATE}
  AND PRODPLAN_SEQ = #{PRODPLAN_SEQ}
  AND PRODWORK_SEQ = #{PRODWORK_SEQ}
  AND WORK_SEQ = #{WORK_SEQ}
  AND PROD_SEQ = #{PROD_SEQ}

