mapper.select:
SELECT rmp.ROLE_MENU_ID as roleMenuId, rmp.GROUP_ID as groupId, rmp.MENU_ID as menuId, rmp.PERMISSION_ID as permissionId, rmp.USE_AT as useAt, rmp.FRST_REGIST_PNTTM as frstRegistPnttm, rmp.FRST_REGISTER_ID as frstRegisterId, rmp.LAST_UPDT_PNTTM as lastUpdtPnttm, rmp.LAST_UPDUSR_ID as lastUpdusrId, g.GROUP_NM as groupNm, m.MENU_NM as menuNm, m.MENU_URL as menuUrl, p.PERMISSION_NM as permissionNm, p.PERMISSION_LEVEL as permissionLevel FROM MES_ROLE_MENU_PERMISSION rmp LEFT JOIN LETTNAUTHORGROUPINFO g ON rmp.GROUP_ID = g.GROUP_ID LEFT JOIN MES_MENU_INFO m ON rmp.MENU_ID = m.MENU_ID LEFT JOIN MES_PERMISSION_TYPE p ON rmp.PERMISSION_ID = p.PERMISSION_ID WHERE rmp.USE_AT = 'Y'
AND rmp.GROUP_ID = #{groupId}
AND rmp.MENU_ID = #{menuId}
AND rmp.PERMISSION_ID = #{permissionId}
ORDER BY g.GROUP_NM, m.MENU_ORDR, rmp.ROLE_MENU_ID

mapper.delete:
DELETE FROM MES_ROLE_MENU_PERMISSION
WHERE GROUP_ID = #{groupId}
AND MENU_ID = #{menuId}

    AND PERMISSION_ID = #{permissionId}
